import { Component, OnInit } from '@angular/core';
import { LugarService } from 'ruta/del/servicio/lugar.service';
import { LugarModel } from 'ruta/del/modelo/lugar.model';

@Component({
  selector: 'app-lugar-admun',
  templateUrl: './lugar-admun.component.html',
  styleUrls: ['./lugar-admun.component.css']
})
export class LugarAdmunComponent implements OnInit {
  lugares: LugarModel[] = [];
  nuevoLugar: LugarModel = {
    nombre: '',
    direccion: '',
    pais: '',
    ciudad: '',
    estado: ''
  };
  lugarEditando: LugarModel | null = null;

  constructor(private lugarService: LugarService) { }

  ngOnInit(): void {
    this.cargarLugares();
  }

  cargarLugares(): void {
    this.lugarService.getLugars().subscribe(
      lugares => {
        this.lugares = lugares;
      },
      error => {
        console.error('Error al cargar lugares:', error);
      }
    );
  }

  crearLugar(): void {
    this.lugarService.addLugar(this.nuevoLugar).subscribe(
      nuevoLugar => {
        this.lugares.push(nuevoLugar);
        this.nuevoLugar = {
          nombre: '',
          direccion: '',
          pais: '',
          ciudad: '',
          estado: ''
        };
      },
      error => {
        console.error('Error al crear lugar:', error);
      }
    );
  }

  eliminarLugar(id: string): void {
    this.lugarService.deleteLugar(id).subscribe(
      () => {
        this.lugares = this.lugares.filter(lugar => lugar._id !== id);
      },
      error => {
        console.error('Error al eliminar lugar:', error);
      }
    );
  }

  editarLugar(lugar: LugarModel): void {
    this.lugarEditando = lugar;
  }

  actualizarLugar(): void {
    if (this.lugarEditando) {
      this.lugarService.updateLugar(this.lugarEditando).subscribe(
        () => {
          this.lugarEditando = null;
        },
        error => {
          console.error('Error al actualizar lugar:', error);
        }
      );
    }
  }
}
