import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';

import { DatosPModel } from './models/datosPModel';

@Injectable({
  providedIn: 'root'
})
export class DatosPService {

  constructor(private http: HttpClient) { }
  // Replace with your actual backend API URL
  baseUrl: string = "http://localhost:3000/api/datosP";

  getDatosP() {
    return this.http.get<DatosPModel[]>(this.baseUrl).pipe(
      map(res => res)
    );
  }

  addDatosP(newDatosP: DatosPModel) {
    return this.http.post<DatosPModel>(this.baseUrl, newDatosP).pipe(
      map(res => res)
    );
  }

  deleteDatosP(id: string) {
    return this.http.delete<DatosPModel>(`${this.baseUrl}/${id}`).pipe(
      map(res => res)
    );
  }

  updateDatosP(newDatosP: DatosPModel) {
    return this.http.put<DatosPModel>(`${this.baseUrl}/${newDatosP._id}`, newDatosP).pipe(
      map(res => res)
    );
  }
}
