// autos.component.ts
import { Component, OnInit } from '@angular/core';
import { AutosService } from 'src/app/services/autos.service';

@Component({
  selector: 'app-autos',
  templateUrl: './autos.component.html',
  styleUrls: ['./autos.component.css']
})
export class AutosComponent implements OnInit {
  autos: any[] = [];

  constructor(private autosService: AutosService) { }

  ngOnInit(): void {
    this.obtenerDatosAutos();
  }

  obtenerDatosAutos() {
    this.autosService.obtenerDatos().subscribe(
      (data: any[]) => {
        // Filtramos los datos para mostrar solo los campos necesarios
        this.autos = data.map(auto => ({
          marca: auto.marca,
          modelo: auto.modelo,
          placas: auto.placas,
          asientos: auto.asientos,
          maletas: auto.maletas,
          tipoCaja: auto.tipoCaja
        }));
      },
      (error) => {
        console.log(error);
      }
    );
  }
}

<img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcREhzyE4hpBjh0Ugo8zXnSA4-3ZMgPfPh4Thw&usqp=CAU" alt="Banner de Autos" class="banner-img">

<div class="title">
  <h1>Catálogo de autos</h1>
</div>

<div class="autos-list">
  <div class="auto" *ngFor="let auto of autos">
    <h3>{{ auto.marca }} {{ auto.modelo }}</h3>
    <p>Marca: {{ auto.marca }}</p>
    <img src="{{ auto.imagen }}" alt="{{ auto.modelo }}">
    <div class="info">
      <p>Características básicas:</p>
      <p>
        <i class="fas fa-car"></i> Tipo de caja: {{ auto.tipoCaja }}
        <i class="fas fa-suitcase"></i> Maletas: {{ auto.maletas }}
        <i *ngIf="auto.aireAcondicionado === 'Si'" class="fas fa-snowflake"></i>
        <i *ngIf="auto.aireAcondicionado !== 'Si'" class="fas fa-lock"></i>      
        <i class="fas fa-user"></i> {{ auto.capacidad }}
        <!-- Agrega más propiedades según lo que necesites mostrar -->
      </p>
      <p><i class="fas fa-id-card"></i> Placas: {{ auto.placas }}</p>
      <button class="btn-reservar" routerLink="/reservas">Reservar</button>
    </div>
  </div>
</div>
