// ciudad.component.ts
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CiudadService } from '../../services/ciudad.service';
import { DatosPService } from '../../services/datos-pservice.service';
import { CuidadModel } from '../../models/datosPModel';
import { DatosPModel } from '../../models/datosPModel';

@Component({
  selector: 'app-ciudad',
  templateUrl: './ciudad.component.html',
  styleUrls: ['./ciudad.component.css']
})
export class CiudadComponent implements OnInit {
  ciudades: CuidadModel[] = [];
  paises: DatosPModel[] = [];
  datosPaises: DatosPModel[] = [];
  ciudadForm: FormGroup;
  editingCiudadId: string | null = null; // Inicializado como null

  constructor(
    private ciudadService: CiudadService,
    private paisService: DatosPService,
    private fb: FormBuilder
  ) {
    this.ciudadForm = this.fb.group({
      _id: [null], // Cambiado de '""' a 'null'
      nombre: ['', Validators.required],
      pais: ['', Validators.required]
    });
  }

  ngOnInit() {
    this.cargarCiudades();
    this.cargarPaises();
  }

  cargarCiudades() {
    this.ciudadService.getDatosP().subscribe(
      data => {
        this.ciudades = data;
        console.log('Ciudades cargadas:', this.ciudades);
      },
      error => {
        console.error('Error al cargar ciudades:', error);
      }
    );
  }

  cargarPaises() {
    this.paisService.getDatosP().subscribe(
      data => {
        this.datosPaises = data; // Actualizar la propiedad datosPaises
        console.log('Datos de países cargados:', this.datosPaises);
      },
      error => {
        console.error('Error al cargar datos de países:', error);
      }
    );
  }

  crearCiudad(event: Event) {
    event.preventDefault();
  
    if (this.ciudadForm.valid) {
      const nuevaCiudad: CuidadModel = {
        nombre: this.ciudadForm.value.nombre,
        pais: this.ciudadForm.value.pais,
      };
  
      this.ciudadService.addDatosP(nuevaCiudad).subscribe(
        data => {
          console.log('Ciudad creada:', data);
          this.ciudadForm.reset();
          this.cargarCiudades();
        },
        error => {
          console.error('Error al crear ciudad:', error);
        }
      );
    } else {
      console.error('Formulario no válido. Por favor, complete todos los campos requeridos.');
    }
  }

  seleccionarCiudad() {
    // Aquí puedes agregar la lógica que deseas al seleccionar una ciudad
    console.log('Ciudad seleccionada');
  }
  cargarDatosP() {
    this.paisService.getDatosP().subscribe(
      data => {
        this.datosPaises = data;
        console.log('Datos cargados:', this.datosPaises);
      },
      error => {
        console.error('Error al cargar datos:', error);
      }
    );
  }
  eliminarCiudad(id: string | undefined) {
    if (id) {
      this.ciudadService.deleteDatosP(id).subscribe(
        data => {
          console.log('Ciudad eliminada:', data);
          this.cargarCiudades();
        },
        error => {
          console.error('Error al eliminar ciudad:', error);
        }
      );
    }
  }

  editarCiudad(ciudad: DatosPModel) {
    this.ciudadForm.patchValue(ciudad);
  }
  
  actualizarCiudad() {
    if (this.ciudadForm.valid) {
      this.ciudadService.updateDatosP(this.ciudadForm.value).subscribe(
        () => {
          this.cargarCiudades();
          this.ciudadForm.reset();
        },
        error => {
          console.error('Error al actualizar país:', error);
        }
      );
    }
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
  <div class="container-fluid">
    <a class="navbar-brand" href="#">RentAuto</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
      aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">autos</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Lugar</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Pais</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="/ciudad">Ciudad</a>
        </li>
      </ul>
    </div>
  </div>
</nav>

<div class="container">
  <br>
  <div class="row">
   <!-- Parte Izquierda - Formulario -->
<div class="col-md-4">
  <div class="card">
    <div class="card-body">
      <h5 class="card-title">Agregar/Editar Ciudad</h5>
      <form [formGroup]="ciudadForm" (ngSubmit)="crearCiudad($event)">
        <div class="form-group">
          <label for="city-name">Nombre de la Ciudad:</label>
          <input type="text" class="form-control" id="city-name" formControlName="nombre" required>
        </div>
        <div class="form-group">
          <label for="country-select">Selecciona un País:</label>
          <select class="form-control" id="country-select" formControlName="pais">
            <option value="" disabled selected>Elige un país</option>
            <option *ngFor="let pais of datosPaises" [value]="pais.nombre">
              {{ pais.nombre }}
            </option>
          </select>
          
        </div>
        <br>
        <button type="submit" class="btn btn-primary">Agregar</button>
        <button type="button" class="btn btn-success ms-2" (click)="actualizarCiudad()">Actualizar</button>
      </form>
    </div>
  </div>
</div>


    <!-- Parte Derecha - Lista de Ciudades -->
    <div class="col-md-8">
      <div class="card">
        <div class="card-body">
          <h5 class="card-title">Ciudades Agregadas</h5>
          <table class="table">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Nombre</th>
                <th scope="col">País</th>
                <th scope="col">Acciones</th>
              </tr>
            </thead>
            <tbody id="country-table-body">
              <tr *ngFor="let ciudad of ciudades; let i = index">
                <th scope="row">{{ i + 1 }}</th>
                <td>{{ ciudad.nombre }}</td>
                <td>{{ ciudad.pais }}</td>
                <td>
                  <button type="button" class="btn btn-sm btn-primary"(click)="editarCiudad(ciudad)">Editar</button>

                  <button type="button" class="btn btn-danger btn-sm ms-2" (click)="eliminarCiudad(ciudad._id)">Eliminar</button>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>


<script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.10.2/dist/umd/popper.min.js"></script>
