import { Component, OnInit } from '@angular/core';
import { ReservaModel } from '../../models/datosPModel';
import { ReservasService } from '../../services/reserva.service';

@Component({
  selector: 'app-home-admin',
  templateUrl: './home-admin.component.html',
  styleUrls: ['./home-admin.component.css']
})
export class HomeAdminComponent implements OnInit {
  reservas: ReservaModel[] = [];

  constructor(private reservasService: ReservasService) { }

  ngOnInit(): void {
    this.cargarReservas();
  }

  cargarReservas() {
    this.reservasService.getReservas().subscribe(
      (data: ReservaModel[]) => {
        this.reservas = data;
        console.log('Reservas cargadas:', this.reservas);
      },
      (error) => {
        console.error('Error al cargar reservas:', error);
      }
    );
  }

  verReserva(reserva: ReservaModel) {
    // Aquí puedes agregar la lógica para mostrar los detalles de la reserva
    // Por ejemplo, podrías abrir un modal con los detalles de la reserva
    console.log('Detalles de la reserva:', reserva);
  }
}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
    <div class="container-fluid">
        <a class="navbar-brand" href="#">RentAuto</a>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav"
            aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
                <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="#">Home</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="#">Autos</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="#">Lugar</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="/pais">Pais</a>
                </li>
                <li class="nav-item">
                    <a class="nav-link" href="/ciudad">Ciudad</a>
                </li>
            </ul>
        </div>
    </div>
</nav>
<br><br>
<div class="container">
    <h2 style="text-align: center; margin-top: 50px;">Reservas</h2>
    <table class="table" style="margin-top: 20px; border-collapse: collapse; border-radius: 8px; overflow: hidden; box-shadow: 0 0 20px rgba(0, 0, 0, 0.1);">
        <thead>
            <tr style="background-color: #333; color: #fff;">
                <th style="padding: 15px; text-align: left;">#</th>
                <th style="padding: 15px; text-align: left;">Cliente</th>
                <th style="padding: 15px; text-align: left;">Teléfono</th>
                <th style="padding: 15px; text-align: left;">Lugar</th>
                <th style="padding: 15px; text-align: left;">Estatus</th>
            </tr>
        </thead>
        <tbody id="country-table-body">
            <tr *ngFor="let reserva of reservas; let i = index">
              <td>{{ i + 1 }}</td>
              <td>{{ reserva.cliente }}</td>
              <td>{{ reserva.telefono }}</td>
              <td>{{ reserva.lugarS }}</td>
              <td>{{ reserva.estatusR }}</td>
              <td>
                <button class="btn btn-primary" (click)="verReserva(reserva)">Ver</button>
              </td>
            </tr>
          </tbody>
    </table>
</div>
